mapper.select:
SELECT SEQUENCE,
       MENU_ID,
       DEPENDENCE_URI
  FROM MENU_DEPENDENCE


       SEQUENCE = #{sequence}


   AND MENU_ID = #{menuId}


   AND DEPENDENCE_URI = #{dependenceUri}

SELECT SEQUENCE,
       MENU_ID,
       DEPENDENCE_URI
  FROM MENU_DEPENDENCE


       SEQUENCE = #{sequence}


   AND MENU_ID = #{menuId}


   AND DEPENDENCE_URI = #{dependenceUri}

mapper.delete:
DELETE FROM MENU_DEPENDENCE
 WHERE MENU_ID = #{menuId}

   AND SEQUENCE = #{sequence}
