mapper.select:
SELECT AUTHORITY_ID,
       ACCESS_MODE
  FROM AUTHORITY_ACCESS


       AUTHORITY_ID = #{authorityId}


   AND ACCESS_MODE = #{accessMode}

SELECT AUTHORITY_ID,
       ACCESS_MODE
  FROM AUTHORITY_ACCESS


       AUTHORITY_ID = #{authorityId}


   AND ACCESS_MODE = #{accessMode}

mapper.delete:
DELETE FROM AUTHORITY_ACCESS
 WHERE AUTHORITY_ID = #{authorityId}

   AND ACCESS_MODE = #{accessMode}
