mapper.select:
SELECT AUTHORITY_ID,
       MENU_ID
  FROM AUTHORITY_MENU


       AUTHORITY_ID = #{authorityId}


   AND MENU_ID = #{menuId}

SELECT AUTHORITY_ID,
       MENU_ID
  FROM AUTHORITY_MENU


       AUTHORITY_ID = #{authorityId}


   AND MENU_ID = #{menuId}

mapper.delete:
DELETE FROM AUTHORITY_MENU
 WHERE AUTHORITY_ID = #{authorityId}

   AND MENU_ID = #{menuId}
