mapper.select:
SELECT CODE_ID,
       PARENT_CODE_ID,
       CODE_NAME,
       DESCRIPTION,
       USE_STATE
  FROM CODE


       CODE_ID = #{codeId}


   AND PARENT_CODE_ID = #{parentCodeId}


   AND USE_STATE = #{useState}


 ORDER BY PARENT_CODE_ID ASC, CODE_ID ASC

