mapper.select:
SELECT SEQUENCE,
       MENU_ID,
       DEPENDENCE_URI
  FROM MENU_DEPENDENCE


       SEQUENCE = #{sequence}


       MENU_ID = #{menuId}

SELECT SEQUENCE,
       MENU_ID,
       DEPENDENCE_URI
  FROM MENU_DEPENDENCE


       SEQUENCE = #{sequence}


       MENU_ID = #{menuId}

mapper.delete:
DELETE FROM MENU_DEPENDENCE
 WHERE MENU_ID = #{menuId}

       SEQUENCE = #{sequence}
