mapper.select:
SELECT MENU_ID, PARENT_MENU_ID, MENU_NAME, URI, DESCRIPTION, DISPLAY_ORDER, USE_STATE FROM MENU
MENU_ID = #{menuId}
AND PARENT_MENU_ID = #{parentMenuId}
AND USE_STATE = #{useState}
ORDER BY PARENT_MENU_ID ASC, DISPLAY_ORDER ASC, MENU_ID ASC

