mapper.select:
SELECT USER_ID,
       AUTHORITY_ID
  FROM USER_AUTHORITY


       USER_ID = #{userId}


   AND AUTHORITY_ID = #{authorityId}

SELECT USER_ID,
       AUTHORITY_ID
  FROM USER_AUTHORITY


       USER_ID = #{userId}


   AND AUTHORITY_ID = #{authorityId}

mapper.delete:
DELETE FROM USER_AUTHORITY
 WHERE USER_ID = #{userId}

   AND AUTHORITY_ID = #{authorityId}
